import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';

import {MatButtonModule} from '@angular/material/button';
import { Observable } from 'rxjs';
import { BehaviorSubject } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { Chat } from 'src/app/model/chat.model';
import { ChatService } from 'src/app/service/chat.service';
import { UserService } from 'src/app/service/user.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent implements OnInit {
  user$ :Observable<Chat> | undefined ;
  refresh_User$ = new BehaviorSubject<boolean>(true);

  cht_shw:boolean = false;
  cht_box:boolean = true;
  adminemail:any ;
  public u_email:any;
  public formData:any
  public formData1:any
  dataChat:any ;
  check_user :boolean =false;
  check_user1:boolean =true;
  constructor(
    public UserServices:UserService,
    public ChatServices:ChatService
  ) { }

  ngOnInit(): void {

    // this.dataChat  = this.refresh_User$.pipe(switchMap(_ => this.ChatServices.getChat()));

    // console.log(" chat data ads",this.dataChat);
    this.ChatServices.getChat().subscribe((data)=>{
        this.dataChat = this.refresh_User$.pipe(switchMap(async (_) => data)) 
        console.log("Data is ",data)
      })



  this.adminemail = localStorage.getItem('email')
  this.u_email = localStorage.getItem('useremail')
    if(this.u_email){
        this.check_user = true
        this.check_user1  = false
    }
    else{
      this.check_user =false
      this.check_user1 =true
    }
  }
  onSubmit1(form1:NgForm){
    this.formData1 = {
      adminemail:this.adminemail,
      useremail:this.u_email,
      usermsg:form1.value.usermsg

    }
    this.ChatServices.PostUser(this.formData1).subscribe((data)=>{
      console.log('Add data',data)
    })
    this.refresh_User$.next(false);
  }
  onSubmit(form:NgForm){
    localStorage.setItem('useremail',form.value['useremail']);
      this.formData = {
        adminemail:this.adminemail,
        useremail:form.value.useremail,
        usermsg:form.value.usermsg

      }
      this.ChatServices.PostUser(this.formData).subscribe((data)=>{
        console.log('Add data',data),
        localStorage.setItem('useremail',form.value['useremail']);

        console.log("local storage user")
        // console.log(JSON.stringify(form.value))
      })

  }
}

  <button mat-stroked-button color="blue" [hidden]="cht_shw" (click)="cht_shw = !cht_shw ; cht_box = !cht_box" id="cht_button">Chat</button>


<div class="col-lg-4">

 <div id="fixed_chat" [hidden]="check_user">
    <mat-card class="example-card" [hidden]="cht_box">
        <p id="cht_head">Chat 	<mat-icon (click)="cht_shw = !cht_shw ; cht_box = !cht_box" id="mini">minimize</mat-icon> </p>
        <mat-card-header>

            <mat-card-subtitle></mat-card-subtitle>
          <div mat-card-avatar class="example-header-image"></div>
          <mat-card-title (click)="cht_shw = !cht_shw ; cht_box = !cht_box">Shiba Inu</mat-card-title>
          <mat-card-subtitle>Dog Breed</mat-card-subtitle>
        </mat-card-header>
        <mat-card-content>
            <form (ngSubmit)="onSubmit(form)" #form="ngForm" >
                <p style="text-align: center;">
                  <mat-form-field appearance="standard"  style="background-color: #e9e9e9;padding: 10px;width: 250px;height: 65px;">

                  <mat-icon matSuffix style="color: black">sentiment_very_satisfied</mat-icon>
                    <mat-label style="color: black">Name</mat-label>
                    <input matInput placeholder="Name" style="color: #2b3ce1 ;"   name="user_name"  ngModel #admin_name>

                  </mat-form-field>
                </p >
                <p style="text-align: center;">
                  <mat-form-field appearance="standard"  style="background-color: #e9e9e9;padding: 10px;width: 250px;height: 65px;">

                  <mat-icon matSuffix style="color: black">email</mat-icon>
                    <mat-label style="color: black">Email</mat-label>
                    <input matInput placeholder="Enter your Email"  style="color: #2b3ce1 !important ;"  name="useremail" ngModel #admin_email>

